import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Dashboard.css";

import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
  PieChart,
  Pie,
  Cell,
} from "recharts";

function Dashboard({ token }) {
  const [user, setUser] = useState({});
  const [transactions, setTransactions] = useState([]);
  const [income, setIncome] = useState(0);
  const [expense, setExpense] = useState(0);
  const navigate = useNavigate();

  const calculateTotals = (transactions) => {
    const inc = transactions
      .filter((t) => t.type === "income")
      .reduce((acc, cur) => acc + cur.amount, 0);
    const exp = transactions
      .filter((t) => t.type === "expense")
      .reduce((acc, cur) => acc + cur.amount, 0);
    setIncome(inc);
    setExpense(exp);
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const userRes = await axios.get("http://localhost:5000/api/users/me", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUser(userRes.data);

        const trxRes = await axios.get(
          "http://localhost:5000/api/transactions",
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        setTransactions(trxRes.data);
        calculateTotals(trxRes.data);
      } catch (err) {
        console.error("Error fetching data:", err);
      }
    };
    fetchData();
  }, [token]);

  const deleteTransaction = async (id) => {
    if (!window.confirm("Are you sure you want to delete this transaction?")) return;
    try {
      await axios.delete(`http://localhost:5000/api/transactions/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const updated = transactions.filter((t) => t._id !== id);
      setTransactions(updated);
      calculateTotals(updated);
    } catch (err) {
      console.error("Error deleting transaction:", err);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/");
  };

  const pieData = [
    { name: "Income", value: income },
    { name: "Expense", value: expense },
  ];
  const pieColors = ["#4caf50", "#f44336"];

  const barData = [{ name: "Total", income, expense }];

  return (
    <div className="dashboard">
      <nav className="navbar">
        <div className="user-info">
          {user.profilePic && (
            <img
              src={user.profilePic}
              alt="profile"
              className="profile-pic"
            />
          )}
          <h2>Welcome, {user.name}</h2>
        </div>
        <div>
          <button className="nav-btn" onClick={() => navigate("/add/income")}>Add Income</button>
          <button className="nav-btn" onClick={() => navigate("/add/expense")}>Add Expense</button>
          <button className="nav-btn logout" onClick={handleLogout}>Sign Out</button>
        </div>
      </nav>

      <div className="summary">
        <div className="spent">
          <h3>SPENT</h3>
          <p>${expense.toLocaleString()}</p>
        </div>
        <div className="triangle-chart">
          ▲
        </div>
        <div className="remaining">
          <h3>REMAINING</h3>
          <p>${(income - expense).toLocaleString()}</p>
        </div>
      </div>

      <div className="charts">
        <div className="bar-chart">
          <h3>Income vs Expense</h3>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={barData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="name" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Bar dataKey="income" fill="#4caf50" />
              <Bar dataKey="expense" fill="#f44336" />
            </BarChart>
          </ResponsiveContainer>
        </div>

        <div className="pie-chart">
          <h3>Total Income vs Expense</h3>
          <ResponsiveContainer width="100%" height={300}>
            <PieChart>
              <Pie
                data={pieData}
                dataKey="value"
                nameKey="name"
                cx="50%"
                cy="50%"
                outerRadius={100}
                label
              >
                {pieData.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={pieColors[index % pieColors.length]} />
                ))}
              </Pie>
            </PieChart>
          </ResponsiveContainer>
        </div>
      </div>

      <div className="expenses-list">
        <h3>Transactions</h3>
        <ul>
          {transactions.map((trx) => (
            <li key={trx._id}>
              <div className="expense-details">
                <div>
                  <strong>{trx.category}</strong> - ₹{trx.amount}
                </div>
                <div>{trx.date.split("T")[0]}</div>
              </div>
              <button className="delete-btn" onClick={() => deleteTransaction(trx._id)}>❌</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Dashboard;
