import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Dashboard.css";

import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
  PieChart,
  Pie,
  Cell,
} from "recharts";

function Dashboard({ token }) {
  const [user, setUser] = useState({});
  const [transactions, setTransactions] = useState([]);
  const [income, setIncome] = useState(0);
  const [expense, setExpense] = useState(0);
  const navigate = useNavigate();

  // Function to recalculate totals
  const calculateTotals = (transactions) => {
    const inc = transactions
      .filter((t) => t.type === "income")
      .reduce((acc, cur) => acc + cur.amount, 0);
    const exp = transactions
      .filter((t) => t.type === "expense")
      .reduce((acc, cur) => acc + cur.amount, 0);
    setIncome(inc);
    setExpense(exp);
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const userRes = await axios.get("http://localhost:5000/api/users/me", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUser(userRes.data);

        const trxRes = await axios.get(
          "http://localhost:5000/api/transactions",
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        setTransactions(trxRes.data);
        calculateTotals(trxRes.data);
      } catch (err) {
        console.error("Error fetching data:", err);
      }
    };

    fetchData();
  }, [token]);

  const deleteTransaction = async (id) => {
    if (!window.confirm("Are you sure you want to delete this transaction?"))
      return;

    try {
      await axios.delete(`http://localhost:5000/api/transactions/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      const updated = transactions.filter((t) => t._id !== id);
      setTransactions(updated);
      calculateTotals(updated); // ✅ Update chart values
    } catch (err) {
      console.error(
        "Error deleting transaction:",
        err.response?.data || err.message
      );
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/");
  };

  const chartData = [{ name: "Total", income, expense }];
  const pieData = [
    { name: "Income", value: income },
    { name: "Expense", value: expense },
  ];
  const pieColors = ["#4caf50", "#f44336"];

  return (
    <div className="dashboard-container">
      <h1>Welcome, {user.name}</h1>
      <button className="logout-btn" onClick={handleLogout}>
        Sign Out
      </button>

      {user.profilePic && (
        <img src={user.profilePic} alt="profile" width="100" />
      )}
      <h2>Total Income: ₹{income}</h2>
      <h2>Total Expense: ₹{expense}</h2>

      <div>
        <button onClick={() => navigate("/add/income")}>Add Income</button>
        <button onClick={() => navigate("/add/expense")}>Add Expense</button>
      </div>

      <h3>Income vs Expense Chart</h3>
      <ResponsiveContainer width="100%" height={300}>
        <BarChart data={chartData}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="name" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Bar dataKey="income" fill="#4caf50" />
          <Bar dataKey="expense" fill="#f44336" />
        </BarChart>
      </ResponsiveContainer>

      <h3>Transaction Breakdown</h3>
      <ResponsiveContainer width="100%" height={300}>
        <PieChart>
          <Pie
            data={pieData}
            dataKey="value"
            nameKey="name"
            cx="50%"
            cy="50%"
            outerRadius={100}
            label
          >
            {pieData.map((entry, index) => (
              <Cell
                key={`cell-${index}`}
                fill={pieColors[index % pieColors.length]}
              />
            ))}
          </Pie>
        </PieChart>
      </ResponsiveContainer>

      <h3>Transactions:</h3>
      <ul className="transaction-list">
        {transactions.map((trx) => (
          <li key={trx._id}>
            <div>
              {trx.date.split("T")[0]} - {trx.type.toUpperCase()}: ₹{trx.amount}{" "}
              ({trx.category})
            </div>
            <button onClick={() => deleteTransaction(trx._id)}>❌</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Dashboard;
