import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Dashboard.css";

import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
  PieChart,
  Pie,
  Cell,
} from "recharts";

function Dashboard({ token }) {
  const [user, setUser] = useState({});
  const [transactions, setTransactions] = useState([]);
  const [income, setIncome] = useState(0);
  const [expense, setExpense] = useState(0);
  const navigate = useNavigate();

  const calculateTotals = (transactions) => {
    const inc = transactions.filter((t) => t.type === "income").reduce((acc, cur) => acc + cur.amount, 0);
    const exp = transactions.filter((t) => t.type === "expense").reduce((acc, cur) => acc + cur.amount, 0);
    setIncome(inc);
    setExpense(exp);
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const userRes = await axios.get("http://localhost:5000/api/users/me", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUser(userRes.data);

        const trxRes = await axios.get("http://localhost:5000/api/transactions", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setTransactions(trxRes.data);
        calculateTotals(trxRes.data);
      } catch (err) {
        console.error("Error fetching data:", err);
      }
    };

    fetchData();
  }, [token]);

  const deleteTransaction = async (id) => {
    if (!window.confirm("Are you sure you want to delete this transaction?")) return;

    try {
      await axios.delete(`http://localhost:5000/api/transactions/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      const updated = transactions.filter((t) => t._id !== id);
      setTransactions(updated);
      calculateTotals(updated);
    } catch (err) {
      console.error("Error deleting transaction:", err.response?.data || err.message);
    }
  };

  const chartData = [{ name: "Total", income, expense }];
  const pieData = transactions.map((t) => ({
    name: t.category,
    value: t.amount,
  }));
  const pieColors = ["#4caf50", "#ff9800", "#9c27b0", "#03a9f4", "#e91e63", "#607d8b"];

  const recentTransactions = transactions.slice(0, 3);

  return (
    <div className="dashboard-container">
      {/* Nav Tabs */}
      <nav className="dashboard-nav">
        <div onClick={() => navigate("/dashboard")} className="nav-item active">DASHBOARD</div>
        <div className="nav-item">SPENT</div>
        <div className="nav-item">INCOME</div>
      </nav>

      {/* Top Summary */}
      <div className="summary-container">
        <div className="summary-spent">
          <p>SPENT</p>
          <h1>₹{expense}</h1>
        </div>

        <div className="summary-graph">
          <ResponsiveContainer width="100%" height={100}>
            <BarChart data={[{ spent: expense, remaining: income - expense }]}>
              <Bar dataKey="spent" stackId="a" fill="#ff5722" />
              <Bar dataKey="remaining" stackId="a" fill="#4caf50" />
            </BarChart>
          </ResponsiveContainer>
        </div>

        <div className="summary-remaining">
          <p>REMAINING</p>
          <h1>₹{income - expense}</h1>
        </div>
      </div>

      {/* Main Content */}
      <div className="content-container">
        {/* Left - Pie Chart */}
        <div className="left-panel">
          <h3>Total Spent</h3>
          <ResponsiveContainer width="100%" height={250}>
            <PieChart>
              <Pie data={pieData} dataKey="value" nameKey="name" cx="50%" cy="50%" outerRadius={80} label>
                {pieData.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={pieColors[index % pieColors.length]} />
                ))}
              </Pie>
            </PieChart>
          </ResponsiveContainer>
        </div>

        {/* Center - Expense Categories */}
        <div className="center-panel">
          <h3>Expenses: Customer visit</h3>
          <table className="expense-table">
            <tbody>
              {transactions.map((trx) => (
                <tr key={trx._id}>
                  <td>{trx.category}</td>
                  <td>₹{trx.amount}</td>
                </tr>
              ))}
            </tbody>
          </table>
          <button className="add-btn" onClick={() => navigate("/add/expense")}>ADD</button>
        </div>

        {/* Right - Recent Transactions */}
        <div className="right-panel">
          {recentTransactions.map((trx) => (
            <div className="transaction-card" key={trx._id}>
              <p className="date">{trx.date.split("T")[0]}</p>
              <h4>{trx.category}</h4>
              <p><span className="spent-label">SPENT</span> ₹{trx.amount}</p>
              <p><span className="remaining-label">TOTAL REMAINING</span> ₹{income - trx.amount}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
