import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Dashboard.css";

import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
  PieChart,
  Pie,
  Cell,
} from "recharts";

function Dashboard({ token }) {
  const [user, setUser] = useState({});
  const [transactions, setTransactions] = useState([]);
  const [income, setIncome] = useState(0);
  const [expense, setExpense] = useState(0);
  const navigate = useNavigate();

  const calculateTotals = (transactions) => {
    const inc = transactions
      .filter((t) => t.type === "income")
      .reduce((acc, cur) => acc + cur.amount, 0);
    const exp = transactions
      .filter((t) => t.type === "expense")
      .reduce((acc, cur) => acc + cur.amount, 0);
    setIncome(inc);
    setExpense(exp);
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const userRes = await axios.get("http://localhost:5000/api/users/me", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUser(userRes.data);

        const trxRes = await axios.get("http://localhost:5000/api/transactions", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setTransactions(trxRes.data);
        calculateTotals(trxRes.data);
      } catch (err) {
        console.error("Error fetching data:", err);
      }
    };

    fetchData();
  }, [token]);

  const deleteTransaction = async (id) => {
    if (!window.confirm("Are you sure you want to delete this transaction?")) return;

    try {
      await axios.delete(`http://localhost:5000/api/transactions/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      const updated = transactions.filter((t) => t._id !== id);
      setTransactions(updated);
      calculateTotals(updated);
    } catch (err) {
      console.error("Error deleting transaction:", err.response?.data || err.message);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/");
  };

  const chartData = [{ name: "Total", income, expense }];
  const pieData = [
    { name: "Income", value: income },
    { name: "Expense", value: expense },
  ];
  const pieColors = ["#4caf50", "#f44336"];

  // New chart: Date-wise transaction
  const dateWiseData = transactions.map((t) => ({
    date: t.date.split("T")[0],
    amount: t.amount,
    type: t.type,
  }));

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h1>Welcome, {user.name}</h1>
        <button className="logout-btn" onClick={handleLogout}>Sign Out</button>
      </div>

      {user.profilePic && (
        <div className="profile-pic-container">
          <img src={user.profilePic} alt="profile" width="80" height="80" />
        </div>
      )}

      {/* Top Summary */}
      <div className="summary-section">
        <div className="spent">
          <p>SPENT</p>
          <h1>₹{expense}</h1>
        </div>

        <div className="triangle-graph">
          <ResponsiveContainer width="100%" height={80}>
            <BarChart data={[{ name: "Stats", spent: expense, remaining: income - expense }]}>
              <Bar dataKey="spent" stackId="a" fill="#f44336" />
              <Bar dataKey="remaining" stackId="a" fill="#4caf50" />
            </BarChart>
          </ResponsiveContainer>
        </div>

        <div className="remaining">
          <p>REMAINING</p>
          <h1>₹{income - expense}</h1>
        </div>
      </div>

      {/* Income/Expense and Add Buttons */}
      <div className="main-content">
        <div className="chart-section">
          <h3>Total Income vs Expense</h3>
          <ResponsiveContainer width="100%" height={250}>
            <BarChart data={chartData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="name" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Bar dataKey="income" fill="#4caf50" />
              <Bar dataKey="expense" fill="#f44336" />
            </BarChart>
          </ResponsiveContainer>
        </div>

        <div className="expense-section">
          <h3>Manage</h3>
          <button onClick={() => navigate("/add/income")}>Add Income</button>
          <button onClick={() => navigate("/add/expense")}>Add Expense</button>
        </div>
      </div>

      {/* Pie Chart */}
      <div className="chart-section">
        <h3>Transaction Breakdown</h3>
        <ResponsiveContainer width="100%" height={300}>
          <PieChart>
            <Pie
              data={pieData}
              dataKey="value"
              nameKey="name"
              cx="50%"
              cy="50%"
              outerRadius={100}
              label
            >
              {pieData.map((entry, index) => (
                <Cell key={`cell-${index}`} fill={pieColors[index % pieColors.length]} />
              ))}
            </Pie>
          </PieChart>
        </ResponsiveContainer>
      </div>

      {/* NEW: Date Wise Transaction Chart */}
      <div className="chart-section">
        <h3>Date-wise Transaction Amount</h3>
        <ResponsiveContainer width="100%" height={300}>
          <BarChart data={dateWiseData}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="date" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Bar dataKey="amount" fill="#2196f3" />
          </BarChart>
        </ResponsiveContainer>
      </div>

      {/* Transactions List */}
      <div className="transaction-section">
        <h3>Transactions</h3>
        <ul className="transaction-list">
          {transactions.map((trx) => (
            <li key={trx._id}>
              <div>
                {trx.date.split("T")[0]} - {trx.type.toUpperCase()}: ₹{trx.amount} ({trx.category})
              </div>
              <button onClick={() => deleteTransaction(trx._id)}>❌</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Dashboard;
