import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Dashboard({ token }) {
    const [user, setUser] = useState({});
    const [transactions, setTransactions] = useState([]);
    const [income, setIncome] = useState(0);
    const [expense, setExpense] = useState(0);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const userRes = await axios.get('http://localhost:5000/api/users/me', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setUser(userRes.data);

                const trxRes = await axios.get('http://localhost:5000/api/transactions', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setTransactions(trxRes.data);

                const inc = trxRes.data
                    .filter(t => t.type === 'income')
                    .reduce((acc, cur) => acc + cur.amount, 0);
                const exp = trxRes.data
                    .filter(t => t.type === 'expense')
                    .reduce((acc, cur) => acc + cur.amount, 0);
                setIncome(inc);
                setExpense(exp);
            } catch (err) {
                console.error('Error fetching data:', err);
            }
        };

        fetchData();
    }, [token]);

    const addTransaction = async (type) => {
        const category = prompt(`${type === 'income' ? 'Income Source' : 'Expense Reason'}:`);
        const date = prompt('Date (YYYY-MM-DD):');
        const amount = parseFloat(prompt('Amount:'));

        if (!category || !date || isNaN(amount)) {
            alert('Invalid input');
            return;
        }

        try {
            await axios.post('http://localhost:5000/api/transactions', {
                type, category, date, amount
            }, {
                headers: { Authorization: `Bearer ${token}` }
            });
            window.location.reload();
        } catch (err) {
            console.error('Error adding transaction:', err);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        navigate('/');
    };

    return (
        <div>
            <h1>Welcome, {user.name}</h1>
            <button onClick={handleLogout}>Sign Out</button>
            {user.profilePic && <img src={user.profilePic} alt="profile" width="100" />}
            <h2>Total Income: ₹{income}</h2>
            <h2>Total Expense: ₹{expense}</h2>
            <button onClick={() => addTransaction('income')}>Add Income</button>
            <button onClick={() => addTransaction('expense')}>Add Expense</button>

            <h3>Transactions:</h3>
            <ul>
                {transactions.map(trx => (
                    <li key={trx._id}>
                        {trx.date.split('T')[0]} - {trx.type.toUpperCase()}: ₹{trx.amount} ({trx.category})
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default Dashboard;
