import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';


function Dashboard({ token }) {
    const [user, setUser] = useState({});
    const [transactions, setTransactions] = useState([]);
    const [income, setIncome] = useState(0);
    const [expense, setExpense] = useState(0);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const userRes = await axios.get('http://localhost:5000/api/users/me', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setUser(userRes.data);

                const trxRes = await axios.get('http://localhost:5000/api/transactions', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setTransactions(trxRes.data);

                const inc = trxRes.data
                    .filter(t => t.type === 'income')
                    .reduce((acc, cur) => acc + cur.amount, 0);
                const exp = trxRes.data
                    .filter(t => t.type === 'expense')
                    .reduce((acc, cur) => acc + cur.amount, 0);
                setIncome(inc);
                setExpense(exp);
            } catch (err) {
                console.error('Error fetching data:', err);
            }
        };

        fetchData();
    }, [token]);

    const deleteTransaction = async (id) => {
        if (!window.confirm('Are you sure you want to delete this transaction?')) return;

        try {
            await axios.delete(`http://localhost:5000/api/transactions/${id}`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setTransactions(transactions.filter(t => t._id !== id));
        } catch (err) {
            console.error('Error deleting transaction:', err);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        navigate('/');
    };

    return (
        <div className="dashboard-container">
            <h1>Welcome, {user.name}</h1>
            <button className="logout-btn" onClick={handleLogout}>Sign Out</button>
            {user.profilePic && <img src={user.profilePic} alt="profile" width="100" />}
            <h2>Total Income: ₹{income}</h2>
            <h2>Total Expense: ₹{expense}</h2>
            <button onClick={() => navigate('/add/income')}>Add Income</button>
            <button onClick={() => navigate('/add/expense')}>Add Expense</button>

            <h3>Transactions:</h3>
            <ul className="transaction-list">
                {transactions.map(trx => (
                    <li key={trx._id}>
                        <div>
                            {trx.date.split('T')[0]} - {trx.type.toUpperCase()}: ₹{trx.amount} ({trx.category})
                        </div>
                        <button onClick={() => deleteTransaction(trx._id)}>❌</button>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default Dashboard;
