import React, { createContext, useContext, useState } from "react";
import axios from "axios";

const BASE_URL = "http://localhost:5000/api/v1/";

const GlobalContext = createContext();

export const GlobalProvider = ({ children }) => {
  const [incomes, setIncomes] = useState([]);
  const [expenses, setExpenses] = useState([]);
  const [error, setError] = useState(null);

  // ---------- LOGIN ----------
  const login = async (email, password) => {
    try {
      const res = await axios.post(`${BASE_URL}auth/login`, { email, password });
      alert(res.data.message);
      localStorage.setItem('token', res.data.token);
    } catch (err) {
      setError(err.response?.data?.message || 'Login failed');
    }
  };

  // ---------- SIGNUP ----------
  const signUp = async (email, password) => {
    try {
      const res = await axios.post(`${BASE_URL}auth/signup`, { email, password });
      alert(res.data.message);
    } catch (err) {
      setError(err.response?.data?.message || 'Signup failed');
    }
  };

  // ---------- INCOME ----------
  const addIncome = async (income) => {
    const token = localStorage.getItem('token');
    const res = await axios.post(`${BASE_URL}add-income`, income, {
      headers: { Authorization: `Bearer ${token}` }
    }).catch((err) => {
      setError(err.response?.data?.message);
    });
    getIncomes();
  };

  const getIncomes = async () => {
    const token = localStorage.getItem('token');
    const res = await axios.get(`${BASE_URL}get-incomes`, {
      headers: { Authorization: `Bearer ${token}` }
    });
    setIncomes(res.data);
  };

  const deleteIncome = async (id) => {
    const token = localStorage.getItem('token');
    await axios.delete(`${BASE_URL}delete-income/${id}`, {
      headers: { Authorization: `Bearer ${token}` }
    });
    getIncomes();
  };

  const totalIncome = () => {
    return incomes.reduce((acc, item) => acc + item.amount, 0);
  };

  // ---------- EXPENSE ----------
  const addExpense = async (expense) => {
    const token = localStorage.getItem('token');
    const res = await axios.post(`${BASE_URL}add-expense`, expense, {
      headers: { Authorization: `Bearer ${token}` }
    }).catch((err) => {
      setError(err.response?.data?.message);
    });
    getExpenses();
  };

  const getExpenses = async () => {
    const token = localStorage.getItem('token');
    const res = await axios.get(`${BASE_URL}get-expenses`, {
      headers: { Authorization: `Bearer ${token}` }
    });
    setExpenses(res.data);
  };

  const deleteExpense = async (id) => {
    const token = localStorage.getItem('token');
    await axios.delete(`${BASE_URL}delete-expense/${id}`, {
      headers: { Authorization: `Bearer ${token}` }
    });
    getExpenses();
  };

  const totalExpenses = () => {
    return expenses.reduce((acc, item) => acc + item.amount, 0);
  };

  // ---------- BALANCE ----------
  const totalBalance = () => {
    return totalIncome() - totalExpenses();
  };

  // ---------- HISTORY ----------
  const transactionHistory = () => {
    const history = [...incomes, ...expenses];
    history.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
    return history.slice(0, 3);
  };

  return (
    <GlobalContext.Provider value={{
      incomes,
      expenses,
      error,
      setError,
      addIncome,
      getIncomes,
      deleteIncome,
      totalIncome,
      addExpense,
      getExpenses,
      deleteExpense,
      totalExpenses,
      totalBalance,
      transactionHistory,
      login,
      signUp
    }}>
      {children}
    </GlobalContext.Provider>
  );
};

export const useGlobalContext = () => {
  return useContext(GlobalContext);
};
