import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Dashboard.css";

import {
  PieChart,
  Pie,
  Cell,
  ResponsiveContainer,
} from "recharts";

function Dashboard({ token }) {
  const [user, setUser] = useState({});
  const [transactions, setTransactions] = useState([]);
  const [income, setIncome] = useState(0);
  const [expense, setExpense] = useState(0);
  const navigate = useNavigate();

  const calculateTotals = (transactions) => {
    const inc = transactions
      .filter((t) => t.type === "income")
      .reduce((acc, cur) => acc + cur.amount, 0);
    const exp = transactions
      .filter((t) => t.type === "expense")
      .reduce((acc, cur) => acc + cur.amount, 0);
    setIncome(inc);
    setExpense(exp);
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const userRes = await axios.get("http://localhost:5000/api/users/me", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUser(userRes.data);

        const trxRes = await axios.get(
          "http://localhost:5000/api/transactions",
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        setTransactions(trxRes.data);
        calculateTotals(trxRes.data);
      } catch (err) {
        console.error("Error fetching data:", err);
      }
    };

    fetchData();
  }, [token]);

  const deleteTransaction = async (id) => {
    if (!window.confirm("Are you sure you want to delete this transaction?")) return;
    try {
      await axios.delete(`http://localhost:5000/api/transactions/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const updated = transactions.filter((t) => t._id !== id);
      setTransactions(updated);
      calculateTotals(updated);
    } catch (err) {
      console.error("Error deleting transaction:", err.response?.data || err.message);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/");
  };

  const spendingCategories = {};
  transactions.forEach((trx) => {
    if (trx.type === "expense") {
      spendingCategories[trx.category] = (spendingCategories[trx.category] || 0) + trx.amount;
    }
  });

  const pieData = Object.entries(spendingCategories).map(([category, amount]) => ({
    name: category,
    value: amount,
  }));

  const pieColors = ["#4caf50", "#f44336", "#ff9800", "#9c27b0", "#00bcd4", "#ffc107"];

  return (
    <div className="dashboard-container">
      {/* Top Navigation */}
      <div className="nav-bar">
        <div className="nav-left">
          <div className="nav-item active">Dashboard</div>
          <div className="nav-item">Spent</div>
          <div className="nav-item">Income</div>
        </div>

        <div className="nav-right">
          {user.profilePic && (
            <img src={user.profilePic} alt="Profile" className="profile-pic" />
          )}
          <span className="profile-name">{user.name}</span>
          <button className="logout-btn" onClick={handleLogout}>Sign Out</button>
        </div>
      </div>

      {/* Spent / Remaining Section */}
      <div className="summary-section">
        <div className="spent">
          <h3>SPENT</h3>
          <h1>‚Çπ{expense}</h1>
        </div>

        <div className="summary-icon">üî∫üîª</div>

        <div className="remaining">
          <h3>REMAINING</h3>
          <h1>‚Çπ{income - expense}</h1>
        </div>
      </div>

      {/* Charts and Expenses */}
      <div className="main-content">
        <div className="left-section">
          <div className="chart-container">
            <h3>Total Spent</h3>
            <ResponsiveContainer width="100%" height={250}>
              <PieChart>
                <Pie
                  data={pieData}
                  dataKey="value"
                  nameKey="name"
                  outerRadius={80}
                  label
                >
                  {pieData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={pieColors[index % pieColors.length]} />
                  ))}
                </Pie>
              </PieChart>
            </ResponsiveContainer>
          </div>

          <div className="expenses-table">
            <h3>Expenses</h3>
            <table>
              <thead>
                <tr>
                  <th>Category</th>
                  <th>Amount</th>
                </tr>
              </thead>
              <tbody>
                {pieData.map((item, idx) => (
                  <tr key={idx}>
                    <td>{item.name}</td>
                    <td>‚Çπ{item.value}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>

        {/* Right Section */}
        <div className="right-section">
          <h3>Recent Transactions</h3>
          {transactions
            .filter((t) => t.type === "expense")
            .map((trx) => (
              <div key={trx._id} className="transaction-card">
                <div>{trx.date.split("T")[0]}</div>
                <div><strong>{trx.category}</strong></div>
                <div style={{ color: "#f44336" }}>Spent: ‚Çπ{trx.amount}</div>
                <div style={{ color: "#4caf50" }}>Remaining: ‚Çπ{income - expense}</div>
                <button onClick={() => deleteTransaction(trx._id)}>‚ùå</button>
              </div>
            ))}
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
